    var state = '';
  
const [details, setDetails] = useState([]);

console.log(details);

  useEffect(() => {
    function getdetails() {
      axios
        .get("http://localhost:3000/withdrawPayment/" + tid)
        .then((res) => {
            setDetails(res.data);
        })
        .catch((err) => {
          alert(err.message);
        });
    }
    getdetails();
  }, []);
    

    return(
        
        
        <div className="WithdrawHome">
         <button className="backbtn" onClick={() => props.history.push("/WithdrawHome")}>
         Back</button>
