                 Bank Details</button><br/>    


        <div>
        {details.map((item) =>
            <p hidden>{state = item.State}</p>
        )} 
            {(() => {
                if (state=='pending') {
                return (
                    <div>
                  <button className="wdh2" onClick={() => props.history.push("/withdraw")} disabled>
                  Payment Withdrawal </button>
                  </div>
                );
                } else if (state==null) {
                return (
                    <div>
                  <button className="wdh2" onClick={() => props.history.push("/withdraw")}>
                  Payment Withdrawal </button>
                  </div>
                );
                } else {
                return (
                    <div>
                    <button className="wdh2" onClick={() => props.history.push("/withdraw")}>
                  Payment Withdrawal </button>
                </div>
                );
                }
            })()}
        )
            </div>
            
