import React, { useState, useEffect } from "react";
import axios from "axios";
import Popup from "./Popup";

export default function PendingPayments(props) {
  const [payment, setPayments] = useState([]);

  const [isOpen, setIsOpen] = useState(false);

  const togglePopup = () => {
    setIsOpen(!isOpen);
  };

  useEffect(() => {
    function getpayments() {
      axios
        .get("http://localhost:3000/pending_payment")
        .then((res) => {
          setPayments(res.data);
        })
        .catch((err) => {
          alert(err.message);
        });
    }
    getpayments();
  }, []);

  return (
    <div>
      <button
        className="backbtn"
        onClick={() => props.history.push("/WithdrawHome")}
      >
        Back
      </button>
      <h1 class="paywitdh1">Pending Payments</h1>

      <div className="box1">
        <table className="withdraw-table">
          <thead>
            <tr>
              <td>Teacher Name</td>
              <td>Bank Details</td>
              <td>Amount</td>
              <td>State</td>
            </tr>
          </thead>

          <tbody>
            {payment.map((item) => (
              <tr>
                <td>{item.Teacher_name}</td>
                <td>
                  <button onClick={togglePopup}>Bank Details</button>
                </td>
                <td>{item.Withdraw_amout}</td>
                <td><input type="checkbox" checked data-toggle="toggle"/></td>
                {console.log(item.Banks[0].Acc_Holder_name)}
                
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}
